mapper.select:
select
log_id, log_access_user_id, log_access_user_name, log_access_type, log_access_url, log_access_ip,
log_access_time
from myproject.t_logger


        and log_id = #{logger.logId}


        and log_access_user_id = #{logger.logAccessUserId}


        and log_access_user_name = #{logger.logAccessUserName}


        and log_access_type = #{logger.logAccessType}


        and log_access_url = #{logger.logAccessUrl}


        and log_access_ip = #{logger.logAccessIp}


        and log_access_time = #{logger.logAccessTime}


ORDER BY log_access_time DESC
LIMIT #{offset}, #{limit}

select
log_id, log_access_user_id, log_access_user_name, log_access_type, log_access_url, log_access_ip,
log_access_time
from myproject.t_logger


        and log_id = #{logId}


        and log_access_user_id = #{logAccessUserId}


        and log_access_user_name = #{logAccessUserName}


        and log_access_type = #{logAccessType}


        and log_access_url = #{logAccessUrl}


        and log_access_ip = #{logAccessIp}


        and log_access_time = #{logAccessTime}
