mapper.select:
select
  note_id, note_created, note_content, note_createtime, note_modifytime, note_title, note_type
from myproject.t_notepad


        and note_type = #{noteType}


order by note_modifytime DESC
limit #{offset}, #{limit}

select
note_id, note_created, note_content, note_createtime, note_modifytime, note_title, note_type
from myproject.t_notepad


        and note_id = #{noteId}


        and note_created = #{noteCreated}


        and note_content = #{noteContent}


        and note_createtime = #{noteCreatetime}


        and note_modifytime = #{noteModifytime}


        and note_title = #{noteTitle}


        and note_type = #{noteType}


order by note_modifytime DESC
